script:
frappe.ui.form.on("Item", "onload", function(frm) {
	excelus_call_show_fields(frm);
});

frappe.ui.form.on("Item", "item_group", function(frm) {
	excelus_call_show_fields(frm);
});

function excelus_call_show_fields(frm) {
	itemgroupname = frm.doc.item_group.replace(/\s+/g, '').toLowerCase();
	console.log(itemgroupname);
	if (itemgroupname == "pm-laminate") {
		excelus_show_fields(frm, 0,1,1,0,1,1,0);
	} else if (itemgroupname == "pm-tape") {
		excelus_show_fields(frm, 0, 0, 1, 0, 0, 1, 0);
	} else if (itemgroupname == "pm-carton") {
		excelus_show_fields(frm, 1, 1, 1, 1, 0, 1, 0);
	} else {
		excelus_show_fields(frm, 0, 0, 0, 0, 0, 0, 1);
	}
}

function excelus_show_fields(frm, packs_per_carton, length, width, height, wastage, thickness, conversion_cost) {
	frm.set_df_property("excelus_packs_per_carton", "hidden", !packs_per_carton);
	frm.set_df_property("excelus_pm_length", "hidden", !length);
	frm.set_df_property("excelus_pm_length_uom", "hidden", !length);
	frm.set_df_property("excelus_pm_width", "hidden", !width);
	frm.set_df_property("excelus_pm_width_uom", "hidden", !width);
	frm.set_df_property("excelus_pm_height", "hidden", !height);
	frm.set_df_property("excelus_pm_height_uom", "hidden", !height);
	frm.set_df_property("excelus_pm_wastage", "hidden", !wastage);
	frm.set_df_property("excelus_pm_thickness", "hidden", !thickness);
	frm.set_df_property("excelus_pm_thickness_uom", "hidden", !thickness);
	frm.set_df_property("excelus_conversion_cost", "hidden", !conversion_cost);
	frm.refresh_fields();
}

frappe.ui.form.on("BOM", "refresh", function(frm) {
	frm.add_custom_button(__('Calc PM Qty'), function(frm){
		frappe.call({
			method: "excelus_erpnext.api.calculate_pm_qtys",
			args: { 
				"item_code": cur_frm.doc.item, 
				"items": cur_frm.doc.items 
			},
			callback: function(r) {
				set_qtys_per_kg(cur_frm, r.message);
				cur_frm.refresh_fields();
			}
		});
	});
});

function set_qtys_per_kg(frm, qtys) {
	for (i=0;i<=frm.doc.items.length-1;i++) {
		if ( frm.doc.items[i]["item_code"].startsWith("PM-T") ) {
			frm.doc.items[i]["qty"] = qtys["tape_qty"];
		} else if ( frm.doc.items[i]["item_code"].startsWith("PM-C") ) {
			frm.doc.items[i]["qty"] = qtys["carton_qty"];
		} else if ( frm.doc.items[i]["item_code"].startsWith("PM-L") ) {
			frm.doc.items[i]["qty"] = qtys["laminate_qty"];
		}
	}
}
